mapper.update:
/* UIUnivCategoryMapper.update */
UPDATE cs_category 
   SET cs_upd_dtime = dbo.FN_NOW_TO_CHAR() ,
       cs_upd_member_seq = #{updMemberSeq, jdbcType=NUMERIC} ,
       cs_upd_ip = #{updIp, jdbcType=VARCHAR} ,
       cs_category_name = #{categoryName, jdbcType=VARCHAR} ,
       cs_sort_order = #{sortOrder, jdbcType=VARCHAR} ,
       cs_category_string = #{categoryString, jdbcType=VARCHAR}
 WHERE cs_category_seq = #{categorySeq} 
       AND cs_delete_yn = 'N'

/* UIUnivCategoryMapper.updateGroupSeq */
UPDATE cs_category 
   SET cs_group_seq = #{categorySeq, jdbcType=NUMERIC} 
 WHERE cs_category_seq = #{categorySeq}
       AND cs_group_level = '1'

/* UIUnivCategoryMapper.updateCategoryString */
UPDATE cs_category 
   SET cs_category_string = #{categoryString, jdbcType=VARCHAR} 
 WHERE cs_category_seq = #{categorySeq}

/* UIUnivCategoryMapper.updateUseCount */
UPDATE cs_category 
   SET 
       cs_use_count = 
       (SELECT COUNT(*) 
         FROM cs_course_master 
        WHERE cs_category_organization_seq = #{categorySeq, jdbcType=NUMERIC} 
              AND cs_delete_yn = 'N' 
       ) + 
       (SELECT COUNT(*) 
         FROM cs_member 
        WHERE cs_category_organization_seq = #{categorySeq, jdbcType=NUMERIC} 
              AND cs_delete_yn = 'N' 
       ) + 
       (SELECT COUNT(*) 
         FROM cs_lcms_contents 
        WHERE cs_category_seq = #{categorySeq, jdbcType=NUMERIC} 
              AND cs_delete_yn = 'N' 
       ) 
 WHERE cs_category_seq = #{categorySeq} 
       AND cs_delete_yn = 'N'

mapper.select:
/* UIUnivCategoryMapper.getList */ SELECT cate.cs_category_seq AS "cate.categorySeq" , cate.cs_category_type_cd AS "cate.categoryTypeCd" , cate.cs_category_string AS "cate.categoryString" , cate.cs_category_name AS "cate.categoryName" , cate.cs_parent_seq AS "cate.parentSeq" , cate.cs_group_seq AS "cate.groupSeq" , cate.cs_group_level AS "cate.groupLevel" , cate.cs_group_order AS "cate.groupOrder" , cate.cs_sort_order AS "cate.sortOrder" , cate.cs_use_count AS "cate.useCount" , cate.cs_mig_category_seq AS "cate.migCategorySeq" , cate.cs_yearterm AS "cate.yearTerm" , cate.cs_delete_yn AS "cate.deleteYn" , cate.cs_reg_member_seq AS "cate.regMemberSeq" , cate.cs_reg_dtime AS "cate.regDtime" , cate.cs_reg_ip AS "cate.regIp" , cate.cs_upd_member_seq AS "cate.updMemberSeq" , cate.cs_upd_dtime AS "cate.updDtime" , cate.cs_upd_ip AS "cate.updIp" , (SELECT COUNT(*) FROM cs_category sub_cate WHERE sub_cate.cs_parent_seq = cate.cs_category_seq AND sub_cate.cs_delete_yn = 'N' ) AS "cate.childCount" FROM cs_category cate WHERE cate.cs_category_type_cd = #{srchCategoryTypeCd} AND cate.cs_parent_seq = #{srchParentSeq}
AND cate.cs_yearterm = #{srchYearTerm}
AND cate.cs_group_level = #{srchGroupLevel}
AND cate.cs_delete_yn = 'N' ORDER BY cate.cs_sort_order ASC

/* UIUnivCategoryMapper.countList */
SELECT COUNT(*) 
  FROM cs_category cate 
 WHERE cate.cs_category_type_cd = #{srchCategoryTypeCd} 
       AND cate.cs_parent_seq = #{srchParentSeq} 
        
       AND cate.cs_yearterm = #{srchYearTerm}
        
        
       AND cate.cs_group_level = #{srchGroupLevel}
        
       AND cate.cs_delete_yn = 'N'

/* UICategoryMapper.getCategoryListAll */
SELECT Z.* FROM (SELECT Row_Number() OVER ( ORDER BY groupSeq, sortOrder ) AS rownumber, * FROM (
SELECT cate.cs_category_seq AS "cate.categorySeq", cate.cs_category_name AS "cate.categoryName", cate.cs_category_string AS "cate.categoryString", ISNULL(member.membercount,0) AS "cate.categoryMemberCount", cate.cs_group_seq AS "groupSeq", cate.cs_sort_order AS "sortOrder" FROM cs_category cate LEFT OUTER JOIN (SELECT cs_category_organization_seq, COUNT(cs_category_organization_seq) AS memberCount FROM cs_member WHERE cs_member_seq NOT IN (SELECT cs_member_seq FROM CS_MEMBER_ADMIN WHERE cs_delete_yn = 'N' ) AND cs_delete_yn = 'N' AND cs_member_status_cd = '${CD.MEMBER_STATUS.APPROVAL}' AND cs_member_ems_type_cd = '${CD.MEMBER_EMP_TYPE.001}' AND cs_student_status_cd = '${CD.STUDENT_STATUS.001}' AND cs_category_organization_seq IS NOT NULL
AND cs_student_year = #{srchStudentYear}
GROUP BY cs_category_organization_seq ) AS member ON member.cs_category_organization_seq = cate.cs_category_seq WHERE cate.cs_delete_yn = 'N' AND cate.cs_group_level = 3
AND cate.cs_category_string LIKE '%' + #{srchCategoryNameDB} + '%'
) Y ) Z WHERE Z.rownumber BETWEEN #{firstItemNo} + 1 AND #{endItemNo} ORDER BY Z.rownumber
