mapper.update:
/* UIUnivCourseActiveBbs.update */
UPDATE cs_course_active_bbs 
   SET cs_upd_dtime = dbo.FN_NOW_TO_CHAR()
   	   ,cs_upd_ip = #{updIp, jdbcType=VARCHAR}
       ,cs_upd_member_seq = #{updMemberSeq, jdbcType=NUMERIC}
        
       ,cs_copy_yn = #{copyYn, jdbcType=VARCHAR}
         
        
       ,cs_evaluate_yn = #{evaluateYn, jdbcType=VARCHAR} 
         
        
       ,cs_popup_yn = #{popupYn, jdbcType=VARCHAR} 
         
        
       ,cs_multi_reg_yn = #{multiRegYn, jdbcType=VARCHAR} 
        
        
        ,cx_prof_view_yn = #{profViewYn, jdbcType=VARCHAR}
        
 WHERE cs_bbs_seq = #{bbsSeq} 
   AND cs_delete_yn = 'N'

/* UIUnivCourseActiveBbs.updateViewCount */
UPDATE cs_bbs 
   SET cs_view_count = cs_view_count + 1 
 WHERE cs_bbs_seq = #{bbsSeq} 
   AND cs_delete_yn = 'N'

/* UIUnivCourseActiveBbs.updateResetPopupYn */
UPDATE cs_course_active_bbs 
   SET cs_popup_yn = 'N' 
 WHERE cs_bbs_seq != #{bbsSeq}

mapper.select:
/* UIUnivCourseActiveBbs.getList */
SELECT Z.* FROM (SELECT Row_Number() OVER (
ORDER BY "bbs.bbsSeq" ASC
ORDER BY "bbs.activeBbsUpdDtime" ASC
ORDER BY "bbs.regDtime" ASC ORDER BY "bbs.regDtime" DESC ORDER BY "bbs.bbsTitle" ASC ORDER BY "bbs.bbsTitle" DESC ORDER BY "bbs.bbsTypeCd" ASC ORDER BY "bbs.bbsTypeCd" DESC ORDER BY "bbs.alwaysTopYn" DESC,"bbs.groupSeq" DESC, "bbs.groupOrder" ASC, "bbs.bbsSeq" ASC
) AS rownumber, * FROM (
SELECT bbs.cs_bbs_seq AS "bbs.bbsSeq" , bbs.cs_board_seq AS "bbs.boardSeq" , bbs.cs_bbs_title AS "bbs.bbsTitle" , bbs.cs_description AS "bbs.description", bbs.cs_bbs_type_cd AS "bbs.bbsTypeCd" , bbs.cs_always_top_yn AS "bbs.alwaysTopYn" , bbs.cs_html_yn AS "bbs.htmlYn" , bbs.cs_secret_yn AS "bbs.secretYn" , activebbs.cs_copy_yn AS "bbs.copyYn" , activebbs.cs_evaluate_yn AS "bbs.evaluateYn" , activebbs.cs_popup_yn AS "bbs.popupYn" , bbs.cs_view_count AS "bbs.viewCount" , bbs.cs_comment_count AS "bbs.commentCount" , bbs.cs_parent_seq AS "bbs.parentSeq" , bbs.cs_group_seq AS "bbs.groupSeq" , bbs.cs_group_level AS "bbs.groupLevel" , bbs.cs_group_order AS "bbs.groupOrder" , bbs.cs_delete_yn AS "bbs.deleteYn" , bbs.cs_reg_member_seq AS "bbs.regMemberSeq" , bbs.cs_reg_dtime AS "bbs.regDtime" , bbs.cs_reg_ip AS "bbs.regIp" , bbs.cs_upd_member_seq AS "bbs.updMemberSeq" , bbs.cs_upd_dtime AS "bbs.updDtime" , bbs.cs_upd_ip AS "bbs.updIp" , member.cs_nickname AS "bbs.regMemberName" , bbs.cx_download_yn AS "bbs.downloadYn", bbs.cs_like_sum AS "bbs.likeSum", member.cs_photo AS "bbs.regMemberPhoto", member.cx_company_name AS "bbs.companyName", member.cx_position AS "bbs.position", activebbs.cx_prof_view_yn AS "bbs.profViewYn", activebbs.cs_upd_dtime AS "bbs.activeBbsUpdDtime" , (SELECT COUNT(*) FROM cs_attach WHERE cs_reference_seq = bbs.cs_bbs_seq AND cs_reference_tablename = 'cs_course_active_bbs' AND cs_delete_yn = 'N' ) AS "bbs.attachCount", CASE WHEN (SELECT COUNT(*) FROM cs_like_history WHERE CS_REFERENCE_TABLENAME = 'cs_bbs' AND CS_DELETE_YN = 'N' AND CS_REG_MEMBER_SEQ = member.cs_member_seq AND CS_REFERENCE_SEQ = bbs.cs_bbs_seq ) > 0 THEN 'Y' ELSE 'N' END as "bbs.likeYN", 'cs_course_active_bbs' AS "referenceTablename", comm.CS_COMMENT_SEQ AS "bbs.commSeq", comm.cs_description AS "bbs.commDescription", comm.cs_reg_dtime AS "bbs.commRegDtime", (SELECT CS_SECRET_YN FROM CS_BOARD WHERE CS_BOARD_SEQ = bbs.CS_BOARD_SEQ) AS "bbs.commSecretYn" FROM cs_course_active_bbs activebbs INNER JOIN cs_bbs bbs ON activebbs.cs_bbs_seq = bbs.cs_bbs_seq AND bbs.cs_delete_yn = 'N' INNER JOIN cs_member member ON bbs.cs_reg_member_seq = member.cs_member_seq INNER JOIN CS_COURSE_ACTIVE active ON activebbs.CS_COURSE_ACTIVE_SEQ = active.CS_COURSE_ACTIVE_SEQ LEFT OUTER JOIN(SELECT TOP 1 * FROM cs_comment ORDER BY cs_reg_dtime DESC) comm ON comm.cs_bbs_seq = bbs.CS_BBS_SEQ WHERE activebbs.cs_course_active_seq = #{srchCourseActiveSeq} AND activebbs.cs_board_type_cd = #{srchBoardTypeCd} AND activebbs.cs_delete_yn = 'N'
AND activebbs.cs_copy_yn = #{srchCopyYn}
AND activebbs.cx_prof_view_yn = #{srchProfViewYn}
AND bbs.cs_board_seq = #{srchBoardSeq}
AND bbs.cs_bbs_type_cd = #{srchBbsTypeCd}
AND bbs.cs_bbs_title LIKE '%' + #{srchWordDB} + '%'
AND bbs.cs_description LIKE '%' + #{srchWordDB} + '%'
AND member.cs_member_name LIKE '%' + #{srchWordDB} + '%'
AND bbs.cs_bbs_seq > #{srchBbsSeq}
AND bbs.cs_bbs_seq < #{srchBbsSeq}
AND activebbs.cs_upd_dtime > #{srchUpdDtime}
AND bbs.cs_always_top_yn = #{srchAlwaysTopYn}
AND bbs.cs_reg_dtime > REPLACE(REPLACE(REPLACE(CONVERT(VARCHAR(100), DATEADD(DD, #{srchRecentDay}, GETDATE()), 120), '-', ''), ':', ''), ' ', '')
AND (member.cs_member_seq = #{srchRegMemberSeq} OR (SELECT subBbs.cs_reg_member_seq FROM cs_course_active_bbs subBbs WHERE subBbs.cs_bbs_seq = bbs.cs_parent_seq AND subBbs.cs_delete_yn = 'N' ) = #{srchRegMemberSeq})
AND bbs.cx_classification_code = #{srchClassificationCode}
ORDER BY activebbs.cs_upd_dtime DESC
ORDER BY bbs.cs_bbs_seq DESC
ORDER BY bbs.cs_bbs_seq DESC
) Y ) Z WHERE Z.rownumber BETWEEN #{firstItemNo} + 1 AND #{endItemNo} ORDER BY Z.rownumber desc desc

	/* UIUnivCourseActiveBbs.countList */
		SELECT COUNT(*)
