						AND bbs.cs_bbs_seq > #{srchBbsSeq}      			
	      			
	      			
	      				AND bbs.cs_bbs_seq < #{srchBbsSeq}
	      			
	      		
	       
	        
				AND activebbs.cs_upd_dtime > #{srchUpdDtime}
	       
	       
