	       		(SELECT subBbs.cs_reg_member_seq 
	       		   FROM cs_course_active_bbs subBbs 
	       		  WHERE subBbs.cs_bbs_seq = bbs.cs_parent_seq 
	       		    AND subBbs.cs_delete_yn = 'N' ) = #{srchRegMemberSeq})
	          
	        
