/* UIUnivCourseActiveBbs.getMyPageBbsAlwaysTopList */ SELECT bbs.cs_bbs_seq AS "bbs.bbsSeq" , bbs.cs_board_seq AS "bbs.boardSeq" , bbs.cs_bbs_title AS "bbs.bbsTitle" , bbs.cs_bbs_type_cd AS "bbs.bbsTypeCd" , bbs.cs_always_top_yn AS "bbs.alwaysTopYn" , bbs.cs_html_yn AS "bbs.htmlYn" , bbs.cs_secret_yn AS "bbs.secretYn" , bbs.cs_view_count AS "bbs.viewCount" , bbs.cs_comment_count AS "bbs.commentCount" , bbs.cs_parent_seq AS "bbs.parentSeq" , bbs.cs_group_seq AS "bbs.groupSeq" , bbs.cs_group_level AS "bbs.groupLevel" , bbs.cs_group_order AS "bbs.groupOrder" , bbs.cs_reg_dtime AS "bbs.regDtime" , member.cs_member_name AS "bbs.regMemberName", b.courseCnt AS "bbs.bbsCount", bbs.cx_download_yn AS "bbs.downloadYn", c.cs_course_active_title AS "course.courseActiveTitle" , c.cs_year AS "course.year", c.cs_yearterm AS "course.yearTerm", SUBSTRING(c.cs_yearterm,5,2) AS "course.term", e.cs_category_type_cd AS "cate.categoryTypeCd", (SELECT COUNT(*) FROM cs_attach WHERE cs_reference_seq = bbs.cs_bbs_seq AND cs_reference_tablename = 'cs_course_active_bbs' AND cs_delete_yn = 'N' ) AS "bbs.attachCount", ISNULL(replyCnt.replyCount, 0) AS "bbs.replyCount" FROM cs_bbs bbs INNER JOIN (SELECT cs_bbs_seq , count(cs_bbs_seq) AS courseCnt , MIN(cs_course_active_seq) AS courseActiveSeq FROM cs_course_active_bbs WHERE cs_delete_yn = 'N' AND cs_board_type_cd = #{srchBoardTypeCd} Group by cs_bbs_seq ) b ON bbs.cs_bbs_seq = b.cs_bbs_seq INNER JOIN cs_member member ON bbs.cs_reg_member_seq = member.cs_member_seq AND member.cs_delete_yn = 'N' INNER JOIN cs_course_active c ON c.cs_course_active_seq = b.courseActiveSeq AND c.cs_delete_yn= 'N' INNER JOIN cs_category e ON e.cs_category_seq = c.cs_category_organization_seq AND e.cs_delete_yn = 'N'
INNER JOIN cs_course_active_lecturer d ON c.cs_course_active_seq = d.cs_course_active_seq AND d.cs_use_yn = 'Y' AND d.cs_delete_yn = 'N' AND d.cs_member_seq = #{srchMemberSeq}
LEFT OUTER JOIN (SELECT sub.cs_parent_seq, COUNT(*) AS replyCount FROM cs_bbs sub WHERE cs_delete_yn = 'N' GROUP BY sub.cs_parent_seq ) replyCnt ON replyCnt.cs_parent_seq = bbs.cs_bbs_seq WHERE bbs.cs_delete_yn = 'N' AND bbs.cs_always_top_yn = 'Y' GROUP BY bbs.cs_bbs_seq , bbs.cs_board_seq , bbs.cs_bbs_title , bbs.cs_bbs_type_cd , bbs.cs_always_top_yn , bbs.cs_html_yn , bbs.cs_secret_yn , bbs.cs_view_count , bbs.cs_comment_count , bbs.cs_parent_seq , bbs.cs_group_seq , bbs.cs_group_level , bbs.cs_group_order , bbs.cs_reg_dtime , member.cs_member_name, b.courseCnt, c.cs_course_active_title, c.cs_year, c.cs_yearterm, e.cs_category_type_cd, replyCnt.replyCount

/* UIUnivCourseActiveBbs.getMyPageBbsList */
SELECT Z.* FROM (SELECT Row_Number() OVER (
ORDER BY "bbs.bbsTitle" ASC ORDER BY "bbs.bbsTitle" DESC ORDER BY "bbs.regDtime" ASC ORDER BY "bbs.regDtime" DESC ORDER BY "bbs.groupSeq" DESC, "bbs.groupOrder" ASC, "bbs.bbsSeq" ASC
) AS rownumber, * FROM (
SELECT bbs.cs_bbs_seq AS "bbs.bbsSeq" , bbs.cs_board_seq AS "bbs.boardSeq" , bbs.cs_bbs_title AS "bbs.bbsTitle" , bbs.cs_bbs_type_cd AS "bbs.bbsTypeCd" , bbs.cs_always_top_yn AS "bbs.alwaysTopYn" , bbs.cs_html_yn AS "bbs.htmlYn" , bbs.cs_secret_yn AS "bbs.secretYn" , bbs.cs_view_count AS "bbs.viewCount" , bbs.cs_comment_count AS "bbs.commentCount" , bbs.cs_parent_seq AS "bbs.parentSeq" , bbs.cs_group_seq AS "bbs.groupSeq" , bbs.cs_group_level AS "bbs.groupLevel" , bbs.cs_group_order AS "bbs.groupOrder" , bbs.cs_reg_dtime AS "bbs.regDtime" , member.cs_member_name AS "bbs.regMemberName", tempBbs.courseCnt AS "bbs.bbsCount", bbs.cx_download_yn AS "bbs.downloadYn", c.cs_course_active_title AS "course.courseActiveTitle" , c.cs_year AS "course.year", c.cs_yearterm AS "course.yearTerm", SUBSTRING(c.cs_yearterm,5,2) AS "course.term", e.cs_category_type_cd AS "cate.categoryTypeCd", (SELECT COUNT(*) FROM cs_attach WHERE cs_reference_seq = bbs.cs_bbs_seq AND cs_reference_tablename = 'cs_course_active_bbs' AND cs_delete_yn = 'N' ) AS "bbs.attachCount", ISNULL(replyCnt.replyCount, 0) AS "bbs.replyCount" FROM cs_bbs bbs INNER JOIN (SELECT activeBbs.cs_bbs_seq , MIN(activeBbs.cs_course_active_seq) AS courseActiveSeq FROM cs_course_active_bbs activeBbs INNER JOIN cs_course_active active ON active.cs_course_active_seq = activeBbs.cs_course_active_seq AND active.cs_delete_yn = 'N' INNER JOIN cs_category category ON category.cs_category_seq = active.cs_category_organization_seq AND category.cs_delete_yn = 'N' WHERE activeBbs.cs_delete_yn = 'N' AND activeBbs.cs_board_type_cd = #{srchBoardTypeCd} AND category.cs_category_type_cd = #{srchCategoryTypeCd}
AND active.cs_course_active_title LIKE '%' + #{srchCourseTitleDB} + '%'
AND active.cs_yearterm = #{srchYearTerm}
AND active.cs_year = #{srchYear}
GROUP BY activeBbs.cs_bbs_seq ) b ON bbs.cs_bbs_seq = b.cs_bbs_seq INNER JOIN cs_member member ON bbs.cs_reg_member_seq = member.cs_member_seq AND member.cs_delete_yn = 'N' INNER JOIN cs_course_active c ON c.cs_course_active_seq = b.courseActiveSeq AND c.cs_delete_yn= 'N' INNER JOIN cs_category e ON e.cs_category_seq = c.cs_category_organization_seq AND e.cs_delete_yn = 'N' INNER JOIN (SELECT cs_bbs_seq , count(cs_bbs_seq) AS courseCnt FROM cs_course_active_bbs WHERE cs_delete_yn = 'N' AND cs_board_type_cd = #{srchBoardTypeCd} GROUP BY cs_bbs_seq ) tempBbs ON bbs.cs_bbs_seq = tempBbs.cs_bbs_seq
INNER JOIN cs_course_active_lecturer d ON c.cs_course_active_seq = d.cs_course_active_seq AND d.cs_use_yn = 'Y' AND d.cs_delete_yn = 'N' AND d.cs_member_seq = #{srchMemberSeq}
LEFT OUTER JOIN (SELECT sub.cs_parent_seq, COUNT(*) AS replyCount FROM cs_bbs sub WHERE cs_delete_yn = 'N' GROUP BY sub.cs_parent_seq ) replyCnt ON replyCnt.cs_parent_seq = bbs.cs_bbs_seq WHERE bbs.cs_delete_yn = 'N'
