) Y ) Z WHERE Z.rownumber BETWEEN #{firstItemNo} + 1 AND #{endItemNo} ORDER BY Z.rownumber

/* UIUnivCourseActiveBbs.countMypageBbsList */ SELECT count(*) FROM ( SELECT bbs.cs_bbs_seq FROM cs_bbs bbs INNER JOIN (SELECT activeBbs.cs_bbs_seq , MIN(activeBbs.cs_course_active_seq) AS courseActiveSeq FROM cs_course_active_bbs activeBbs INNER JOIN cs_course_active active ON active.cs_course_active_seq = activeBbs.cs_course_active_seq AND active.cs_delete_yn = 'N' INNER JOIN cs_category category ON category.cs_category_seq = active.cs_category_organization_seq AND category.cs_delete_yn = 'N' WHERE activeBbs.cs_delete_yn = 'N' AND activeBbs.cs_board_type_cd = #{srchBoardTypeCd} AND category.cs_category_type_cd = #{srchCategoryTypeCd}
AND active.cs_course_active_title LIKE '%' + #{srchCourseTitleDB} + '%'
AND active.cs_yearterm = #{srchYearTerm}
AND active.cs_year = #{srchYear}
GROUP BY activeBbs.cs_bbs_seq ) b ON bbs.cs_bbs_seq = b.cs_bbs_seq INNER JOIN cs_member member ON bbs.cs_reg_member_seq = member.cs_member_seq AND member.cs_delete_yn = 'N' INNER JOIN cs_course_active c ON c.cs_course_active_seq = b.courseActiveSeq AND c.cs_delete_yn= 'N' INNER JOIN cs_category e ON e.cs_category_seq = c.cs_category_organization_seq AND e.cs_delete_yn = 'N' INNER JOIN (SELECT cs_bbs_seq , count(cs_bbs_seq) AS courseCnt FROM cs_course_active_bbs WHERE cs_delete_yn = 'N' AND cs_board_type_cd = #{srchBoardTypeCd} GROUP BY cs_bbs_seq ) tempBbs ON bbs.cs_bbs_seq = tempBbs.cs_bbs_seq
INNER JOIN cs_course_active_lecturer d ON c.cs_course_active_seq = d.cs_course_active_seq AND d.cs_use_yn = 'Y' AND d.cs_delete_yn = 'N' AND d.cs_member_seq = #{srchMemberSeq}
LEFT OUTER JOIN (SELECT sub.cs_parent_seq, COUNT(*) AS replyCount FROM cs_bbs sub WHERE cs_delete_yn = 'N' GROUP BY sub.cs_parent_seq ) replyCnt ON replyCnt.cs_parent_seq = bbs.cs_bbs_seq WHERE bbs.cs_delete_yn = 'N'
AND bbs.cs_board_seq = #{srchBoardSeq}
AND bbs.cs_bbs_type_cd = #{srchBbsTypeCd}
AND bbs.cs_bbs_title LIKE '%' + #{srchWordDB} + '%'
AND bbs.cs_description LIKE '%' + #{srchWordDB} + '%'
AND member.cs_member_name LIKE '%' + #{srchWordDB} + '%'
AND bbs.cs_always_top_yn = #{srchAlwaysTopYn}
AND bbs.cs_group_level = 1 AND ISNULL(replyCnt.replyCount, 0) = 0
GROUP BY bbs.cs_bbs_seq) bbs

mapper.delete:
	/* UIUnivCourseActiveBbs.dropMyPageCourseActiveBbs */
	DELETE 
		FROM cs_course_active_bbs
	WHERE	
 		cs_delete_yn = 'N'
	AND cs_bbs_seq = #{bbsSeq}
